      equal(count, 0);
      equal(get(proxy, 'foo'), 'foo');

      set(content, 'foo', 'bar');

      equal(count, 1);
      equal(get(proxy, 'foo'), 'bar');

      set(proxy, 'foo', 'foo');

      equal(count, 2);
      equal(get(content, 'foo'), 'foo');
      equal(get(proxy, 'foo'), 'foo');

      removeObserver(proxy, 'foo', observer);

      set(content, 'foo', 'bar');

      equal(get(proxy, 'foo'), 'bar');

      set(proxy, 'foo', 'foo');

      equal(get(content, 'foo'), 'foo');
      equal(get(proxy, 'foo'), 'foo');
